import React from 'react';

function YourWorkEverywhere() {
  return (
    <section className="max-w-7xl mx-auto px-6 sm:px-10 py-16 bg-[#0B2F6A] text-white text-center">
      <h2 className="text-3xl font-bold max-w-3xl mx-auto mb-4">
        Your work, everywhere you are
      </h2>
      <p className="max-w-3xl mx-auto text-[#D1D5DB] mb-8 text-sm sm:text-base">
        Access your projects and collaborate from any device, anytime, anywhere.
      </p>
      <button className="bg-[#3B82F6] text-white font-semibold rounded-md px-6 py-2 hover:bg-[#2563eb] transition">
        Get Started
      </button>
    </section>
  );
}

export default YourWorkEverywhere;
